import { Formik, FormikProps } from "formik";
import React, { useState } from "react";
import Button from "../UI/Form/Button/Button";
import Input from "../UI/Form/Input/Input";
import ApiService from "../../services/api/api";

import formValidationSchema from "./FormValidation";
import { useDispatch } from "react-redux";
import { setAllTasks } from "../../services/store/actions/tasksActions";

const AddTask: React.FC = () => {
  const [addTaskForm, setAddTaskForm] = useState<any>({
    id: "",
    title: "",
    description: "",
  });

  const dispatch = useDispatch();

  const addTask = () => {
    ApiService.addTask(19, addTaskForm.title, "cccc");
    // ApiService.getAllTasks();
    dispatch(setAllTasks());
  };

  const onChangeInputValue = (value: string, fieldName?: string) => {
    if (!fieldName) return;
    // console.log(fieldName);
    if (fieldName === "title") {
      setAddTaskForm({
        ...addTaskForm,
        [fieldName]: value,
      });
      // console.log("idddddddddd field", addTaskForm.title);
    }
    if (fieldName === "description") {
      setAddTaskForm({
        ...addTaskForm,
        description: value,
      });
      console.log("desc field", addTaskForm);
    }
  };

  return (
    <div className="add-patient d-flex align-items-center justify-content-center">
      <div className="form-container d-flexflex-column justify-content-center align-items-center">
        <Formik
          initialValues={addTaskForm}
          enableReinitialize
          validationSchema={formValidationSchema()}
          onSubmit={addTask}
        >
          {({ handleSubmit }: FormikProps<any>) => (
            <form
              onSubmit={handleSubmit}
              className="mt-4 mt-sm-0"
              autoComplete="off"
            >
              <div className="d-flex flex-column align-self-stretch align-self-sm-end">
                <Input type="text" name="id" placeholder={`enter task id`} />
                <Input
                  type="text"
                  name="title"
                  placeholder={`enter task title`}
                  onChangeValue={onChangeInputValue}
                />
                <Input
                  type="text"
                  name="description"
                  placeholder={`enter task description`}
                />
              </div>
              <Button type="submit" name={`add task`} />
            </form>
          )}
        </Formik>
      </div>
    </div>
  );
};

export default AddTask;
