import ApiService from "../../api/api";

// let allTasks: any = [];

// const getAllTasks = async (): Promise<any> => {
//   allTasks = await ApiService.getAllTasks();

//   console.log("from get", allTasks.tasks);
// };

export const setAllTasks = () => async (dispatch: any) => {
  let allTasks;
  try {
    allTasks = await ApiService.getAllTasks();
    console.log(allTasks);
    dispatch({
      type: "INIT",
      payload: [
        {
          id: 1,
          title: "Learn React",
          description: "Learn how to use react in building web app",
        },
        {
          id: 2,
          title: "Learn Node",
          description: "Learn how to use node in building server",
        },
        {
          id: 3,
          title: "Learn Array Manipulation",
          description: "Learn how to manipulate arrays in javascript",
        },
        {
          id: 4,
          title: "Learn Laravel",
          description: "Learn how to use laravel",
        },
        {
          id: 5,
          title: "Learn Python",
          description: "Learn how to use python",
        },
      ],
    });
  } catch (err) {
    console.log(err);
  }
};

export const setAllTasks1 = async (): Promise<any> => {
  const allTasks = await ApiService.getAllTasks();
  //   console.log(allTasks.tasks);
  //   await getAllTasks();
  console.log("from set", allTasks);
  return {
    type: "INIT",
    payload: allTasks,
  };
};
