const initialState: any = {
  tasks: [],
};

const allTasks = (state = initialState, action: any) => {
  switch (action.type) {
    case "ALL-TASKS":
      //   console.log(action);
      return {
        ...state,
        tasks: action.payload,
      };

    case "ADD-TASK":
      console.log(action.payload);
      return {
        ...state,
        tasks: state.tasks.push(action.payload),
      };

    default:
      return state;
  }
};

export default allTasks;
