import ApiService from "../../api/api";

// let allTasks: any = [];

// const getAllTasks = async (): Promise<any> => {
//   allTasks = await ApiService.getAllTasks();

//   console.log("from get", allTasks.tasks);
// };

export const setAllTasks = () => async (dispatch: any) => {
  let allTasks;
  try {
    allTasks = await ApiService.getAllTasks();
    console.log(allTasks);
    dispatch({
      type: "INIT",
      payload: allTasks.tasks,
    });
  } catch (err) {
    console.log(err);
  }
};

export const setAllTasks1 = async (): Promise<any> => {
  const allTasks = await ApiService.getAllTasks();
  //   console.log(allTasks.tasks);
  //   await getAllTasks();
  console.log("from set", allTasks);
  return {
    type: "INIT",
    payload: allTasks,
  };
};
