import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import ApiService from "../../services/api/api";
import {
  setAllTasks,
  deleteTask,
  editTask,
} from "../../services/store/actions/tasksActions";
import AddTask from "../AddTask/AddTask";
import Button from "../UI/Form/Button/Button";

const Table: React.FC = () => {
  // const [tasks, setTasks] = useState<any[]>([]);
  const [editedTask, setEditedTask] = useState({});

  const tasks = useSelector((state: any) => state.tasks.tasks);
  const [searchTerm, setSearchTerm] = useState("");
  const dispatch = useDispatch();

  const fetchTasks = async () => {
    // fetch("http://localhost:5000/api/tasks")
    //   .then((res) => res.json())
    //   .then((data) => {
    //     console.log(data);
    //   });
    // ApiService.getAllTasks().then((res) => {
    //   setTasks(res.tasks);
    //   console.log(tasks);
    // });
    // console.log("tasks", allTasks);
    const allTasks = await ApiService.getAllTasks();
    // console.log("todoss", allTasks);
    // setTasks(allTasks.tasks);
    // console.log("all tasks", tasks);
  };

  useEffect(() => {
    // fetchTasks();
    dispatch(setAllTasks());
    // dispatch(deleteTask(5));
  }, []);

  const deleteTaskHandler = (id: any) => {
    // console.log("idddd", id);
    // ApiService.deleteTask(id);
    // fetchTasks();
    dispatch(deleteTask(id));
  };

  // let xxx;
  const editTaskHandler = async (id: number) => {
    // const response = await ApiService.findTaskById(id);
    // console.log(response.task);
    // await setEditedTask(response.task);
    // console.log(editedTask);
    dispatch(editTask(id));
  };

  return (
    <div>
      <div className="add-task-form">
        <AddTask />
      </div>
      <hr />
      <div>
        <h6>search by title</h6>
        <input
          type="text"
          placeholder={`filter by title`}
          onChange={(e) => {
            setSearchTerm(e.target.value);
          }}
        />
      </div>
      <div className="table-content">
        <table className="table">
          <thead>
            <tr>
              <th>Task Id</th>
              <th>Task Title</th>
              <th>Task Description</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {tasks
              .filter((val: any) => {
                if (searchTerm === "") {
                  return val;
                } else if (
                  val.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
                  val.title.includes(searchTerm.toLowerCase())
                ) {
                  return val;
                }
              })
              .map((task: any) => {
                return (
                  <tr key={task.id}>
                    <td>{task.id}</td>
                    <td>{task.title}</td>
                    <td>{task.description}</td>
                    <td className="d-flex justify-content-around align-items-center">
                      <Button
                        name="add"
                        type="button"
                        btnType="back"
                        className="back"
                        saveData={() => {
                          console.log("add");
                        }}
                      />
                      <Button
                        name="edit"
                        saveData={() => {
                          // setEditTask(!editTask);
                          // console.log(editTask);
                          // ApiService.editTask(task.id, "xxx", "3333");
                          editTaskHandler(task.id);
                        }}
                      />
                      <Button
                        name="delete"
                        saveData={() => {
                          deleteTaskHandler(task.id);
                          // dispatch(deleteTask(task.id));
                          // ApiService.deleteTask(task.id);
                          // ApiService.getAllTasks();
                        }}
                      />
                    </td>
                  </tr>
                );
              })}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Table;
